import * as actiontype from '../Action/Action'

export default function BidUserReducer(state=[],action){
    var bids=action.payload.bidlist
    switch(action.type){
        case actiontype.LOAD_LOGGED_USER_BIDS:return [...state,action.payload.bidlist]
        default:return state
    }
}
